import React from 'react'
import ReactDOM from 'react-dom'

const Course = (props) =>{
  return (
  <div>
    <Header course={props.course.name} />
    <Content course={props.course.parts} />
    <Total course={props.course.parts} />
  </div>
  )
 }
const Header = props =>
  <h1>{props.course}</h1>

const Total = props => {
  console.log(props)
  const total = props.course.reduce((s,p) => {
      s += p.exercises
      return s
    }, 0)  
  return (
    <b> total of {total} exercises</b>
  )
}


const Part = props =>
  <p>{props.part.name} {props.part.exercises}</p>

const Content = props => {
  return (
    <div>
      {props.course.map(part => <Part key ={part.id} part={part} />)}
    </div>
  )
} 

const App = () => {
  const course = {
    name: 'Half Stack development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id:1
      },
      {
        name: 'Usin props to pass data',
        exercises: 7,
        id:2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id:3
      },
      {
        name: 'Redux',
        exercises: 11,
        id:4
      }
    ]
  } 

  return (
    <div>
      <Course course={course} />

    </div>
  )
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
